targets.expr:
(
  min by (pod) (
    kube_controller_pod{namespace="$namespace", controller="deploy/$app"}
    * on (pod) group_right()
    min_over_time(kube_pod_status_phase{namespace="$namespace", pod=~"$pod", phase="Running"}[$__rate_interval])
  ) == 1
) * 0

(
  min by (pod, phase) (
    kube_controller_pod{namespace="$namespace", controller="deploy/$app"}
    * on (pod) group_right()
    min_over_time(kube_pod_status_phase{namespace="$namespace", pod=~"$pod", phase="Pending"}[$__rate_interval])
  ) == 1
) * 1

(
  min by (pod, phase) (
    kube_controller_pod{namespace="$namespace", controller="deploy/$app"}
    * on (pod) group_right()
    min_over_time(kube_pod_status_phase{namespace="$namespace", pod=~"$pod", phase="Unknown"}[$__rate_interval])
  ) == 1
) * 2

(
  min by (pod, phase) (
    kube_controller_pod{namespace="$namespace", controller="deploy/$app"}
    * on (pod) group_right()
    min_over_time(kube_pod_status_phase{namespace="$namespace", pod=~"$pod", phase="Failed"}[$__rate_interval])
  ) == 1
) * 3

(
  min by (pod, phase) (
    kube_controller_pod{namespace="$namespace", controller="deploy/$app"}
    * on (pod) group_right()
    min_over_time(kube_pod_status_phase{namespace="$namespace", pod=~"$pod", phase="Succeeded"}[$__rate_interval])
  ) == 1
) * 4

(
  min by (pod) (
    kube_controller_pod{node=~"$node", namespace="$namespace", controller="deploy/$app"}
    * on (pod) group_right()
    min by (pod) (min_over_time(kube_pod_container_status_ready{namespace="$namespace", pod=~"$pod"}[$__rate_interval]))
  ) == 0
) + 5

sum by(pod) (
  kube_controller_pod{namespace="$namespace", controller="deploy/$app", pod=~"$pod"}
  * on (pod)
  sum by (pod) (avg_over_time(container_memory_working_set_bytes{namespace="$namespace", container="$app", pod=~"$pod"}[$__rate_interval]))
)

sum by(pod) (
  kube_controller_pod{namespace="$namespace", controller="deploy/$app", pod=~"$pod"}
  * on (pod)
  sum by (pod) (rate(container_cpu_usage_seconds_total{container!="POD", pod=~"$pod", namespace="$namespace"}[$__rate_interval]))
)
