targets.expr:
sum (
  increase(
    price_monitoring_benchmarks_transitions_total{
      namespace="vsppm-${environment}",
      location_id =~ "$location",      
      status = "started"
    }[$period]
  )
) or vector(0)

sum (
  increase(
    price_monitoring_benchmarks_transitions_total{
      namespace="vsppm-${environment}",
      location_id =~ "$location",      
      status = "finished"
    }[$period]
  )
) or vector(0)

sum (
  increase(
    price_monitoring_benchmarks_transitions_total{
      namespace="vsppm-${environment}",
      location_id =~ "$location",  
      status = "wrong"
    }[$period]
  )
) or vector(0)

sum (
  increase(
    price_monitoring_benchmarks_transitions_total{
      namespace="vsppm-${environment}",
      location_id =~ "$location",      
      status = "failed"
    }[$period]
  )
) or vector(0)

max_over_time(
  sum(rate(price_monitoring_benchmarks_transitions_total{
    namespace="vsppm-${environment}",
    status="stored",
    location_id =~ "$location"
  }[5m]))[$period:]
)

sum (
  increase(
    price_monitoring_benchmarks_transitions_total{
      namespace="vsppm-${environment}",
      location_id =~ "$location",      
      status = "stored"
    }[$period]
  )
) or vector(0)

sum (
  increase(
    price_monitoring_benchmarks_transitions_total{
      namespace="vsppm-${environment}",
      location_id =~ "$location",      
      status = "reported"
    }[$period]
  )
) or vector(0)

sum by (status) (
  increase(
    price_monitoring_legacy_benchmarks_transitions_total{
      namespace="vsppm-${environment}",
      location_id =~ "$location",
      status="completed"   
    }[$period]
  )
)

sum by (cause) (
  increase(
    price_monitoring_legacy_benchmarks_transitions_total{
      namespace="vsppm-${environment}",
      location_id =~ "$location",
      status="failed"   
    }[$period]
  )
)

sum by (cause) (
  increase(
    price_monitoring_legacy_benchmarks_transitions_total{
      namespace="vsppm-${environment}",
      location_id =~ "$location",
      status=~"failed|skipped"
    }[$period]
  )
)

 sum by (location_id) (
  increase(
    price_monitoring_legacy_benchmarks_transitions_total{namespace="vsppm-${environment}", status=~"skipped", location_id =~ "$location"}[$period]
) or vector(0)
)

 sum by (location_id) (
  increase(
    price_monitoring_legacy_benchmarks_transitions_total{namespace="vsppm-${environment}", status=~"failed", location_id =~ "$location"}[$period]
) or vector(0)
)

 sum by (location_id) (
  increase(
    price_monitoring_legacy_benchmarks_transitions_total{namespace="vsppm-${environment}", status=~"completed", location_id =~ "$location"}[$period]
) or vector(0)
)

sum(
  increase(
    price_monitoring_legacy_competitor_prices_transitions_total{
      namespace="vsppm-${environment}",
      location_id =~ "$location",
      status="gathered"   
    }[$period]
  )
)

 sum by (location_id) (
