targets.expr:
sum by(pod) (
  kube_controller_pod{namespace="$namespace", controller="deploy/$app", pod=~"$pod"}
  * on (pod)
  sum by (pod) (rate(container_cpu_usage_seconds_total{container!="POD", pod=~"$pod", namespace="$namespace"}[$__rate_interval]))
)

100 * sum by(pod) (
  kube_controller_pod{namespace="$namespace", controller="deploy/$app", pod=~"$pod"}
  * on (pod)
  sum by(pod) (rate(container_cpu_usage_seconds_total{container!="POD", pod=~"$pod", namespace="$namespace"}[$__rate_interval]))
)
/
avg by(pod) (
  kube_pod_container_resource_requests{resource="cpu", namespace="$namespace", container="$app"}
)

sum by(pod) (
  kube_controller_pod{namespace="$namespace", controller="deploy/$app", pod=~"$pod"}
  * on (pod)
  sum by (pod) (avg_over_time(container_memory_working_set_bytes{namespace="$namespace", container="$app", pod=~"$pod"}[$__rate_interval]))
)
